    private void ANTENATALSaveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ANTENATALSaveButtonActionPerformed
        // TODO add your handling code here:
        SimpleDateFormat sFormat = new SimpleDateFormat("yyyy-MM-dd");
       
        String cardNo = ANTENATALCardNoTextField.getText();
        String phone = ANTENATALPhoneNumberTextField.getText();
        String occupation = ANTENATALOccupationTextField.getText();
        String addr = ANTENATALAddressTextField.getText();
        
        String mStatus = (String) ANTENATALmaritalComboBox.getSelectedItem();
        String religion = (String) ANTENATALreligioncombobox.getSelectedItem();
        String nationality = (String)ANTENATALnationalityComboBox.getSelectedItem();
        String state = (String)ANTENATALstateComboBox.getSelectedItem();
        String spouseName = ANTENATALspouseNameTextField1.getText();
        String spouseAddr = ANTENATALspouseAddressTextField.getText();
        String spousePhone = ANTENATALspousephoneTextField.getText();
        String spouseOccu = ANTENATALspouseOccupationTextField.getText();
        String spousereligion = (String) ANTENATALspousereligioncombobox1.getSelectedItem();
        String spousenationality = (String)ANTENATAspouseLnationalityComboBox1.getSelectedItem();       
        String contact1Name = ANTENATALemergencycontact1nameTextField.getText();
        String contact1phone = ANTENATALemergencycontact1phoneTextField.getText();
        String contact1relation = ANTENATALemergencycontact1relationshipTextField.getText();
        String contact2Name = ANTENATALemergencycontact2nameTextField.getText();
        String contact2phone = ANTENATALemergencycontact2phoneTextField.getText();
        String contact2relation = ANTENATALemergencycontact2relationshipTextField.getText();
        if (!validated(cardNo, phone,occupation, addr,mStatus, religion, nationality, state, spouseName, spouseAddr, spousePhone, spouseOccu, spousereligion, spousenationality, contact1Name, contact1phone, contact1relation, contact2Name, contact2phone, contact2relation))
                {
            JOptionPane.showMessageDialog(loginFrame, "Fill in all Spaces");
                }
        else
        {
            Date dateOfstart = ANTENATALStartDateChooser.getDate();
            String dateOfRegstart = sFormat.format(dateOfstart);
            int age = Integer.parseInt(ANTENATALAgeTextField.getText());
            
            DBoperations db = new DBoperations();
        db.ANTENATALcardsave (cardNo, phone, addr, occupation, dateOfRegstart, mStatus, age, religion, nationality, state, spouseName, spouseAddr, 
             spousePhone, spouseOccu,spousenationality,spousereligion,contact1Name,contact1phone,contact1relation,contact2Name,contact2phone,contact2relation);
        
        ANTENATALCardNoTextField.setText("");
        ANTENATALPhoneNumberTextField.setText("");
        ANTENATALOccupationTextField.setText("");
        ANTENATALAddressTextField.setText("");
        ANTENATALStartDateChooser.setDate(null);
        ANTENATALAgeTextField.setText("");
        ANTENATALmaritalComboBox.setSelectedItem("Marital Status");
        ANTENATALreligioncombobox.setSelectedItem("Select Religion");
        ANTENATALnationalityComboBox.setSelectedItem("Select Nationality");
        ANTENATALstateComboBox.setSelectedItem("Select State");
        ANTENATALspouseNameTextField1.setText("");
        ANTENATALspouseAddressTextField.setText("");
        ANTENATALspousephoneTextField.setText("");
        ANTENATALspouseOccupationTextField.setText("");
        ANTENATALspousereligioncombobox1.setSelectedItem(null);
        ANTENATAspouseLnationalityComboBox1.setSelectedItem(null);
        ANTENATALemergencycontact1nameTextField.setText("");
        ANTENATALemergencycontact1phoneTextField.setText("");
        ANTENATALemergencycontact1relationshipTextField.setText("");
        ANTENATALemergencycontact2nameTextField.setText("");
        ANTENATALemergencycontact2phoneTextField.setText("");
        ANTENATALemergencycontact2relationshipTextField.setText(""); 
       }
    }//GEN-LAST:event_ANTENATALSaveButtonActionPerformed

private boolean 
validated(String cardNo,String phone, String occupation ,String addr ,String mStatus,String religion, 
String nationality, String state ,String spouseName, String spouseAddr, String spousePhone,String spouseOccu ,String spousereligion,
String spousenationality,String contact1Name,String contact1phone, String contact1relation, String contact2Name,String contact2phone,String contact2relation)
  {
    if(cardNo.isEmpty() || phone.isEmpty()||occupation.isEmpty() || addr.isEmpty()||mStatus.isEmpty()||religion.isEmpty() || nationality.isEmpty()||
       state.isEmpty() || spouseName.isEmpty()||spouseAddr.isEmpty() || spouseOccu.isEmpty()||spousePhone.isEmpty()||spousenationality.isEmpty()||spousereligion.isEmpty() || contact1Name.isEmpty()||contact1phone.isEmpty()||contact1relation.isEmpty()||
       contact2Name.isEmpty()||contact2phone.isEmpty()||contact2relation.isEmpty())
    {
        return false;
    }
    return true;
    }

